(function($){$.fn.phImage=function(options){var settings=$.extend({remove_current:true,padding:"default",background_position:null,background_repeat:"no-repeat"},options);return this.each(function(){var element=$(this);var bg_image=element.attr("data-ph-image");if(bg_image){if(settings.remove_current){element.attr("placeholder","");element.on("focus",function(){element.css("background-image","none");});element.on("blur",function(){if(element.val()==""){element.css("background-image","url('"+bg_image+"')");}});}else{if(settings.padding!="default"){element.css("padding",settings.padding);}}if(!settings.remove_current||element.val()==""){element.css({"background-image":"url('"+bg_image+"')","background-repeat":settings.background_repeat});}else{element.css("background-repeat",settings.background_repeat);}if(settings.background_position){element.css("background-position",settings.background_position);}}});};}(jQuery));